import './App.css';
import io from 'socket.io-client'
import { useEffect, useState} from "react";
import NameButtons from './nameButtons';
const socket = io.connect("http://localhost:3001")

function App() {
  // states
  const [message, setMessage] = useState("");
  const [messageReceived, setMessageReceived] = useState("");
  const [textSection, setTextSection] = useState("Welcome to Dream Game.\nPlease select your name:");
  const [name, setName] = useState("");

  const [players, setPlayers] = useState([]);

  // player come online
  const playerJoin = (data) => {
    setName(data);
    setPlayers(...players, name);
    //socket.emit("send_message", { message, name });
  };


  // send to socket
  const sendMessage = () => {
    socket.emit("send_message", { message, name });
  };

  // receive from socket
  useEffect(() => {
    socket.on("receive_message", (data) => {
      var message = data.message;
      var name = data.name;
      setMessageReceived(data.name + ": " + data.message);
    });
  }, [socket]);

  // display
  return (
    <div className="App">
      <p>{textSection}</p>

      <div id="NameButtons">
            <button onClick={() => props.playerJoin("Ethan")}>Ethan</button>
            <button onClick={() => props.setName("Cole")}>Cole</button>
            <button onClick={() => props.setName("Nathan")}>Nathan</button>
        </div>

      <h1>Name: {name}</h1>

      <input 
        placeholder="Message..." 
        onChange={(event) => {
          setMessage(event.target.value);
        }}
      />
      <button onClick={sendMessage}> Send Message</button>

      <h1>Message:</h1>
      {messageReceived}


    </div>
  );
}

export default App;
