import React from "react";

function PlayerSection(props) {

    let playerSection = [];
    for (let i = 0; i < props.scores.length; i++) {
        for (let j = 0; j < props.stats.length; j++) {
            if (props.scores[i][1] === props.stats[j][0]){
                let correct  = props.stats[j][1];
                let incorrect = props.stats[j][2];
                let ratio = (correct/(incorrect+correct+0.000000001)*100).toFixed(2);
                let longestStreak = props.stats[j][3];
                let skillRating = (ratio * (correct/10 + longestStreak)).toFixed(0);
                let temp = props.scores[i];
                temp.unshift(skillRating);
                playerSection.push(temp);
            }
        }
    }

    console.log(playerSection);

    let colThree = "";
    if (props.status === "CHANGE THIS VALUE TO 'before' WHEN STATS ADDED") {
        colThree = "Elo"
    } else if (props.status === "during") {
        colThree = "Status"
    } else if (props.status === "after") {
        colThree = "Guessed"
    } else {
        colThree = "Status"
    }

    if (!(props.name === "")){
        if (Array.isArray(props.scores)) {
            props.scores.sort((a, b) => b[2] - a[2]); 
            return (
                <table className="table">
                    <thead>
                        <tr>
                            <th scope="col">Player</th>
                            <th scope="col">Scores</th>
                            <th scope="col">{colThree}</th>
                        </tr>
                    </thead>
                    <tbody>
                        {props.scores.map((item) => {
                            let itemThree = item[3];
                            if (props.status === "after") {
                                itemThree = item[4];
                            } else if (props.status === "before") {
                                itemThree = playerSection[-1];
                            }
                            return (
                            <tr scope="row">
                                <td>{item[1]}</td>
                                <td>{item[2]}</td>
                                <td>{itemThree}</td>
                            </tr>
                            );
                        })}
                    </tbody>
                </table>
            );
        } else {
            return(<p>ERROR: SCORES NOT ARRAY</p>);
        }
    }
};
export default PlayerSection;
