import './App.css';
import io from 'socket.io-client'
import { useEffect, useState} from "react";
import NameButtons from './nameButtons';
const socket = io.connect("http://localhost:3001")

function App() {
  // states
  const [message, setMessage] = useState("");
  const [messageReceived, setMessageReceived] = useState("");
  const [textSection, setTextSection] = useState("Welcome to Dream Game.\nPlease select your name:");
  const [name, setName] = useState("");

  const [players, setPlayers] = useState([]);
  //setPlayers(players.concat("test"));


  // player come online
  const playerJoin = (data) => {
    setName(data);
    setPlayers(players.concat(data));
    //socket.emit("send_message", { message, name });
  };


  // send to socket
  const sendMessage = () => {
    socket.emit("send_message", { message, name });
  };

  // receive from socket
  useEffect(() => {
    socket.on("receive_message", (data) => {
      var message = data.message;
      var name = data.name;
      setMessageReceived(data.name + ": " + data.message);
    });
  }, [socket]);

  // display
  return (
    <div className="App">

      <p>{textSection}</p>


      <NameButtons players={players} playerJoin={playerJoin} />

      <h1>Name: {name}</h1>

      <input 
        placeholder="Message..." 
        onChange={(event) => {
          setMessage(event.target.value);
        }}
      />
      <button onClick={sendMessage}> Send Message</button>

      <h1>Message:</h1>
      {messageReceived}

      <div>
        {players}
      </div>

    </div>
  );
}

export default App;

